using BepInEx;
using HarmonyLib;
using UnityEngine;

namespace BlueprintsX
{
    [BepInPlugin(pluginGuid, pluginName, pluginVersion)]
    [BepInIncompatibility("com.metalted.zeepkist.blueprintsplus")]

    public class BlueprintXPlugin : BaseUnityPlugin
    {
        public const string pluginGuid = "com.metalted.zeepkist.blueprintsX";
        public const string pluginName = "Blueprints X";
        public const string pluginVersion = "1.5.4";

        private static string markerImage = "";
        private static string fileImage = "";
        private static string fileSwitchImage = "";
        private static string blackPixel = "iVBORw0KGgoAAAANSUhEUgAAAAIAAAACCAIAAAD91JpzAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAAITcAACE3ATNYn3oAAAAMSURBVBhXY0ACDAwAAA4AAXqxuTAAAAAASUVORK5CYII=";
        public static Sprite markerSprite;
        public static Sprite fileSprite;
        public static Sprite fileSwitchSprite;
        public static Sprite blackPixelSprite;

        public void Awake()
        {
            Harmony harmony = new Harmony(pluginGuid);
            harmony.PatchAll();
            Logger.LogInfo($"Plugin {pluginName} is loaded!");

            BPXConfig.InitializeConfig(Config);

            markerSprite = BPXUtilities.Base64ToSprite(markerImage);
            fileSprite = BPXUtilities.Base64ToSprite(fileImage);
            fileSwitchSprite = BPXUtilities.Base64ToSprite(fileSwitchImage);
            blackPixelSprite = BPXUtilities.Base64ToSprite(blackPixel);
        }

        public void Update()
        {
            BPXManager.Run();
        }

        public void OnGUI()
        {
            BPXManager.MainGUICall();
        }

        public void OnApplicationFocus(bool hasFocus)
        {
            if (hasFocus)
            {
                BPXManager.MainApplicationFocus();
            }
            else
            {
                BPXManager.MainApplicationFocusLost();
            }
        }
    }

    [HarmonyPatch(typeof(MainMenuUI), "Awake")]
    public class MainMenuStart
    { 
        public static void Prefix()
        {
            BPXConfig.ForceReload();
        }    
    }

    [HarmonyPatch(typeof(LEV_LevelEditorCentral), "Awake")]
    public class LEV_LevelEditorCentral_Awake
    {
        public static void Postfix(LEV_LevelEditorCentral __instance)
        {
            BPXManager.LevelEditorAwake(__instance);
        }
    }

    [HarmonyPatch(typeof(SkyboxManager), "PreviousCurrent")]
    public class SkyboxManager_PreviousCurrent
    {
        public static bool Prefix(SkyboxManager __instance)
        {
            if(BPXUI.savePanelIsOpen)
            {
                return false;
            }

            return true;
        }
    }

    [HarmonyPatch(typeof(SkyboxManager), "AdvanceCurrent")]
    public class SkyboxManager_AdvanceCurrent
    {
        public static bool Prefix(SkyboxManager __instance)
        {
            if (BPXUI.savePanelIsOpen)
            {
                return false;
            }

            return true;
        }
    }

    [HarmonyPatch(typeof(LEV_Selection), "DeselectAllBlocks")]
    public class Selection_DeselectAll
    {
        public static void Postfix()
        {
            BPXGizmoManager.ResetGizmoSelection();
        }
    }

    [HarmonyPatch(typeof(LEV_PauseMenu), "EnablePauseMenu")]
    public class LEV_PauseMenu_OnOpen
    {
        public static void Prefix()
        {
            if(BPXUI.savePanelIsOpen)
            {
                BPXUI.CloseSavePanel();
            }
        }
    }
}
